    }

    return (
        <form>
            <TextField
                label="Title"
                name="title"
                value={inputs.title}
                variant="standard"
                onChange={handleChange}
                fullWidth
            />
            <br></br>
            <br></br>
            <TextField
                label="Description"
                name="description"
                value={inputs.description}
                variant="standard"
                multiline
                rows={4}
                fullWidth
            />
            <br></br>
            <TextField
                label="Your Content here "
                name="content"
                value={inputs.content}
                multiline
                variant = "standard"
                rows={20}
                fullWidth
            />
            <br></br>
            <br></br>
            <Button 
                variant="outlined"
                onSubmit={handleSubmit}
            > 
                  Post your new article ! 
            </Button>
        </form>
    )
}
